import { useEffect, useState } from 'react'
import {
  GetSchedules,
  CreateSchedules,
  GetScheduleById
} from '../services/ScheduleServices'
import { useNavigate } from 'react-router-dom'
import { useParams } from 'react-router-dom'

const Schedule = ({
  user,
  authenticated,
  setSchedule,
  schedule,
  scheduleExists,
  setScheduleExists
}) => {
  const initialState = {
    date: ''
  }
  let { id } = useParams()
  let navigate = useNavigate()
  const [formState, setFormState] = useState(initialState)
  // const [scheduleExists, setScheduleExists] = useState(false)
  // const [schedule, setSchedule] = useState({})

  const handleChange = (e) => {
    setFormState({ ...formState, [e.target.id]: e.target.value })
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    const response = await CreateSchedules({ ...formState, patron_Id: user.id })
    console.log('handle submit', response)
    setSchedule(response)
    setFormState(initialState)
    setScheduleExists(true)
  }

  useEffect(() => {
    const handleSchedule = async () => {
      console.log(user)
      const data = await GetScheduleById(user.id)
      setSchedule(data)
      console.log(data)
      console.log('handle schedule')
    }
    handleSchedule()
  }, [scheduleExists])

  if (scheduleExists) {
    return (
      <div className="schedule-text">
        <div className="section-title">SCHEDULE </div>
        <div>Date: {schedule.date} </div>
        <br />
        <div className="exhibit-list">
          <div className="exhbit-title">Scheduled Exhibits </div>
          {schedule?.exhibits?.map((exhibit) => (
            <p>{exhibit.name}</p>
          ))}
        </div>
        <button>Delete Schedule</button>
        <button onClick={() => navigate('/exhibit')}>View Exhibits</button>
      </div>
    )
  } else {
    return user && authenticated ? (
      <div className="schedule-text section-title">
        {' '}
        CREATE A SCHEDULE
        <form className="form" onSubmit={handleSubmit}>
          <label className="label dateField" htmlFor="date">
            Date:{' '}
          </label>
          <input
            className="input"
            type="text"
            id="date"
            placeholder="MM/DD/YY (Required)"
            cols="30"
            onChange={handleChange}
            value={formState.date}
            required
          />
          <button className="submit-button" type="submit">
            Create Schedule
          </button>
        </form>
      </div>
    ) : (
      <div className="protected">
        <h3>Oops! You must be signed in to do that!</h3>
        <button onClick={() => navigate('/signin')}>Sign In</button>
      </div>
    )
  }
}

export default Schedule

